mapper.select:
SELECT name, profile, introduce, description, instagram_id, is_display, view, is_about, collaboration_date
FROM artist
WHERE instagram_id = #{instagramId};

SELECT name, profile, introduce, description, instagram_id, is_display, view, is_about, collaboration_date
FROM artist
WHERE collaboration_date > #{startDate}
AND collaboration_date < #{endDate}
LIMIT #{start}, #{end};

SELECT name, profile, introduce, description, instagram_id, is_display, view, is_about, collaboration_date
FROM artist
LIMIT #{start}, #{end};

SELECT name, profile, introduce, description, instagram_id, is_display, view, is_about, collaboration_date
FROM artist
WHERE instagram_id like concat('%', #{instagramId}, '%')
AND collaboration_date > #{startDate}
AND collaboration_date < #{endDate}
LIMIT #{start}, #{end};

SELECT name, profile, introduce, description, instagram_id, is_display, view, is_about, collaboration_date
FROM artist
WHERE name like concat('%', #{name}, '%')
AND collaboration_date > #{startDate}
AND collaboration_date < #{endDate}
LIMIT #{start}, #{end};

mapper.update:
UPDATE artist
SET name = #{name}
WHERE instagram_id = #{instagramId};

UPDATE artist
SET introduce = #{introduce}
WHERE instagram_id = #{instagramId};

UPDATE artist
SET description = #{description}
WHERE instagram_id = #{instagramId};

UPDATE artist
SET profile = #{profile}
WHERE instagram_id = #{instagramId};
